var AdvSearchLoaded = false;
jQuery("#keyword").autocomplete("/topics/search-suggest/",{
    max:10,
    width:366,
    multipleSeparator: ' ',  
	dataType: 'json',
	selectFirst: false,
	parse: function(data) {
		var rows = [];
		for(var i=0; i<data.length; i++){
			rows[rows.length] = { 
				data:data[i], 
				value:data[i].search_keyword, 
				result:data[i].search_keyword 
			}; 
        }
		return rows;
	},
	formatItem: function(item) {
		return format(item);
	}
}).result(function(e, item) {
	// fill value into hide field
});
function format(row) {
	return "" + row.search_count + "個結果"+row.search_keyword;
}
function showHideAdvSearch(){
	var advsearch = jQuery("#AdvSearch");
	if(!AdvSearchLoaded){
		advsearch.load("/topics/advanced-search?team_id=657&sort_id=0&orderby=date-desc");
		AdvSearchLoaded = true;
	}
	advsearch.slideToggle(function(){$("#searchMode").text(advsearch.is(":visible") ? "▲" : "▼");});
}
showHideAdvSearch();

